def convertToVersion2(data):
  try:
    splitData = data.split(':')
    if len(splitData) == 3:
      return data

    dimensions = splitData[0]
    pixels = splitData[1]

    width = int(dimensions.split(',')[0])
    height = int(dimensions.split(',')[1])
  except:
    print ("Error: Invalid picture dimensions")
    return

  if len(pixels) != width*height:
    print ("Error: Num pixels doesn't match dimensions")
    return None

  newPixels = []
  for pixel in pixels:
    color = COLORS[int(pixel)]
    newPixels.append(color)

  return "v2:{},{}:{}".format(width, height, json.dumps(newPixels))
  
def drawPicture(width, height, data):
  pixelWidth = 1
  data = json.loads(data)
  for y in range(height):
    for x in range(width):
      pygame.draw.rect(gameDisplay, data[(y * width)+ x] , (x * pixelWidth, y * pixelWidth, pixelWidth, pixelWidth))
      
def decodeAndDrawPicture(data):
  # see if we have the dimensions specified.  They will be encoded in the
  # format width,height followed by a colon and any amount of white space

  try:
    splitData = data.split(':')
    if len(splitData)!= 3:
      print("Error: Need to use at least version2 data format")

    version = splitData[0]
    dimensions = splitData[1]
    pixels = splitData[2]

    width = int(dimensions.split(',')[0])
    height = int(dimensions.split(',')[1])
  except:
    print ("Error: Invalid picture dimensions")
    return
    
  drawPicture(width, height, pixels)

def makeWider(data, howWide):
  if howWide <=0:
    print("yo, make this a reasonable width")
    
  try:
    splitData = data.split(':')
    if len(splitData)!= 3:
      print("Error: Need to use at least version2 data format")

    version = splitData[0]
    dimensions = splitData[1]
    pixels = json.loads(splitData[2])

    width = int(dimensions.split(',')[0])
    height = int(dimensions.split(',')[1])
  except:
    print ("Error: Invalid picture dimensions")
    return

  result = []
  for i in range(len(pixels)):
    for j in range(howWide):
      result.append(pixels[i])
  width = width * howWide

  return "{}:{},{}:{}".format(version, width, height, json.dumps(result))

def makeNarrower(data, howNarrow):
  if howNarrow <=0:
    print("yo, make this a reasonable width")
    
  try:
    splitData = data.split(':')
    if len(splitData)!= 3:
      print("Error: Need to use at least version2 data format")

    version = splitData[0]
    dimensions = splitData[1]
    pixels = json.loads(splitData[2])

    width = int(dimensions.split(',')[0])
    height = int(dimensions.split(',')[1])
  except:
    print ("Error: Invalid picture dimensions")
    return

  # see what the new width would be because we might need to
  # throw away some pixels
  newWidth = int(width / howNarrow)
  throwawayPixels = width - (newWidth * howNarrow)

  result = []
  # go through the pixels and take the average of narrowCount entries
  for row in range(height):
    sumPixel = None
    continued = False
    for col in range(width):
      # print(col)
      if col > width - 1 - throwawayPixels:
        if col % howNarrow == 0:
          avgPixel = [int(sumPixel[0]/howNarrow), int(sumPixel[1]/howNarrow), int(sumPixel[2]/howNarrow)]
          result.append(avgPixel)  
        continued = True
        # print("continued")
        continue
      if col % howNarrow == 0:
        if col != 0:
          avgPixel = [int(sumPixel[0]/howNarrow), int(sumPixel[1]/howNarrow), int(sumPixel[2]/howNarrow)]
          result.append(avgPixel)
        
        sumPixel = pixels[row * width + col]
      else:
        sumPixel = [sumPixel[0] + pixels[row * width + col][0],
                    sumPixel[1] + pixels[row * width + col][1],
                    sumPixel[2] + pixels[row * width + col][2]]
    if not continued:
      avgPixel = [int(sumPixel[0]/howNarrow), int(sumPixel[1]/howNarrow), int(sumPixel[2]/howNarrow)]
      result.append(avgPixel)  
  return "{}:{},{}:{}".format(version, newWidth, height, json.dumps(result))

def makeHigher(data, howHigh):
  if howHigh <= 0:
    print("yo, use a reasonable height")
  try:
    splitData = data.split(':')
    if len(splitData)!= 3:
      print("Error: Need to use at least version2 data format")

    version = splitData[0]
    dimensions = splitData[1]
    pixels = json.loads(splitData[2])

    width = int(dimensions.split(',')[0])
    height = int(dimensions.split(',')[1])
  except:
    print ("Error: Invalid picture dimensions")
    return

  result = []
  for row in range(height):
    for j in range(howHigh):
      result.extend(pixels[row*width:row*width + width])

  height = height * howHigh

  return "{}:{},{}:{}".format(version, width, height, json.dumps(result))


